mapper.select:
	select fb_num, fb_subject, fb_content, fb_date,fb_readCount,userId
  from freeBoard
   where fb_num = #{fb_num}

mapper.update:
update freeBoard
 set
  fb_subject= #{fb_subject},
  fb_content = #{fb_content}
 where fb_num = #{fb_num}

mapper.select:
	select fb_num,fb_subject,fb_content,userId,fb_date,fb_readCount
   from (
           select fb_num,fb_subject,fb_content,userId,fb_date,fb_readCount,
               row_number() over(order by fb_num desc) as rNum
           from freeBoard
           ) mb
       where rNum between #{rowStart} and #{rowEnd}
           order by fb_num desc;

select fb_num, fb_subject, fb_content, userId, fb_date, fb_readCount
       from (
           select fb_num, fb_subject, fb_content, userId, fb_date, fb_readCount,
               row_number() over(order by fb_num desc) as rNum
           from freeBoard

   		) mb 
   	where rNum between #{rowStart} and #{rowEnd}
       	order by fb_num desc
